  `conf_title` varchar(50) DEFAULT NULL COMMENT '标题',
  `conf_name` varchar(50) DEFAULT NULL COMMENT '变量名',
  `conf_content` varchar(255) DEFAULT NULL,
  `conf_order` int(11) DEFAULT NULL COMMENT '排序',
